class cmdbContainer(DbModuleContainer):

    finder_abs = providers.Factory(DbQuery, model_name=CMDBModel, condt=f'cmdb.user_id == {id}')

class AccountContainer(DbModuleContainer):

    finder_abs = providers.Factory(DbQuery, model_name=AccountModel, condt=f'account.user_id == {id}')
    
class modulecontainer(containers.DeclarativeContainer):

    quotaprovider = providers.Factory(quotaChecker, DbQuery=cmdbContainer, DbQuery=AccountContainer)`
