       'LOCATION': 'redis://default:XXXX@private-redis-XXXX.db.ondigitalocean.com:25061',
       'OPTIONS': {
           'DB': 0,
           # 'PASSWORD': 'XXXX',
           'PICKLE_VERSION': -1,
           "COMPRESSOR": "django_redis.compressors.zlib.ZlibCompressor",
           "REDIS_CLIENT_KWARGS": {"ssl": True},
