For reference, this is what I use to read the entire DB in C++ using heap stack:

```
std::vector<MMDB_search_node_s> stack(1);
{
    MMDB_search_node_s &sn = stack.back();
    sn.left_record_type = MMDB_RECORD_TYPE_EMPTY;
    sn.right_record_type = MMDB_RECORD_TYPE_SEARCH_NODE;
    sn.right_record = 0;
}
for(;;) {
    MMDB_search_node_s &sn = stack.back();
    switch(sn.left_record_type) {
        case MMDB_RECORD_TYPE_EMPTY: break;
        case MMDB_RECORD_TYPE_SEARCH_NODE:
            sn.left_record_type = MMDB_RECORD_TYPE_EMPTY;
            {
                MMDB_search_node_s pushsn;
                mmdb_throw_on_error("MMDB_read_node", MMDB_read_node(&db, sn.left_record, &pushsn));
                stack.push_back(pushsn);
            }
            continue;
        case MMDB_RECORD_TYPE_DATA: process(sn.left_record_entry); break;
        default: THROW;
    }
    switch(sn.right_record_type) {
        case MMDB_RECORD_TYPE_EMPTY: break;
        case MMDB_RECORD_TYPE_SEARCH_NODE:
            mmdb_throw_on_error("MMDB_read_node", MMDB_read_node(&db, sn.right_record, &sn));
            continue;
        case MMDB_RECORD_TYPE_DATA: process(sn.right_record_entry); break;
        default: THROW;
    }
    stack.resize(stack.size()-1);
    if(stack.empty()) {
        break;
    }
}
