            pmgr.add_password(None, server, username, password)
            handler = urllib2.HTTPBasicAuthHandler(pmgr)
            url_opener = urllib2.build_opener(handler)
            try:
                response = url_opener.open(url)
                r = response.read()
                file   = cwd + "\PassdownData.zip"
                output = open(file,"wb")
                output.write(r)
                output.close()
                r = "Successfully downloaded PassdownData.zip from " + tool + " and saved it to " + file # print ( )
                result = messagebox.showinfo("Done", r)
                print (result)
                if result == "ok":
                    root.destroy()
            except Exception as e:
                s = "Downloading PassdownData.zip from %s failed with error %s" % (tool, e) + ". Please verify your credentials. Would you like to try again?" #print ( )
                result2 = messagebox.askyesno("Failure", s)
                print (result2)
                if result2 == False:
                    root.destroy() #doing self.destroy() will clear the box. could use that for refresh stuff...


    def _delpass(self):
        try:
